import React,{Component} from 'react'
import {singlePost,remove} from "./apiPost"
import DefaultPost from "../images/avatar.jpg"
import {isAuthenticated} from "../auth"
import {Link,Redirect} from 'react-router-dom'
class SinglePost extends Component{
    state={
        post:'',
        redirectToHome:false
    }

    componentDidMount=()=>{
        const postId=this.props.match.params.postId
        singlePost(postId).then(data=>{
            if(data.error){
                console.log(data.error)
            }
            else{
                this.setState({post:data})
            }
        })
    }

    renderPost=(post=>{
        const posterId=post.postedBy
        ? `/user/${post.postedBy._id}`
        :"";
        const posterName=post.postedBy
        ? post.postedBy.posterName
        :"Unknown"

        return( <div className="card col-md-8" style={{width:"18rem"}}>

        <div className="card-body">
        <img src={`${process.env.REACT_APP_API_URL}/post/photo/${post._id}`}
        alt={post.title}
        onError={i=>(i.target.src= `${DefaultPost}`)}
        className="img-thumbnail mb-3"
        style={{height:"300px", width:"100%",objectFit:'cover'}}/>

</div>
         <p className="card-text"> {post.body}</p>
         <br/>
         <p className="font-italic mark">
             Posted by{" "}  
             <Link 
                 to={`${posterId}`} 
                >{posterName}{" "}
                </Link>
                on {new Date(post.created).toDateString()}
                </p>
                <div className="d-inline-block">
                <Link 
             to={`/`}
             className="btn btn-raised btn-primary btn-sm">
                 Return to home page
             </Link>



             {isAuthenticated().user && 
                    isAuthenticated().user._id=== post.postedBy._id  && (
                    <>

             <button className="btn btn-raised btn-warning mr-5">
                 Update Post
             </button>

             <button onClick={this.deletePost}className="btn btn-raised btn-warning mr-5">
                Delete Post
             </button>
           </>

                    )
    }

                </div>
                </div>)

         })


            deletePost = () => {
                const postId = this.props.match.params.postId;
                const token = isAuthenticated().token;
                remove(postId, token).then(data => {
                    if (data.error) {
                        console.log(data.error);
                    } else {
                        this.setState({ redirectToHome: true });
                    }
                });
            };





    render(){
        if(this.state.redirectToHome){
            return <Redirect to={`/'`} />;

        }
        const {post}=this.state
        return (
            <div className="container">
                <h2 className="display-2 mt-5 mb-5"> {post.title}</h2>
                {!post ? (<div className="jumbotron text-center">
                    <h2> Loading...</h2>
                    </div>):(


                    this.renderPost(post))}
            </div>
        )
    }
}

export default SinglePost;
