import React,{Component} from 'react'
import {read} from "./apiUser"
import {isAuthenticated} from "../auth"
import {Redirect} from "react-router-dom"
import {update} from "./apiUser"

import DefaultProfile from "../images/avatar.jpg"

class EditProfile extends Component{
    constructor()
    {
        super()
        this.state={
            id:"",
            name:"",
            email:"",
            password:"",
            error:"",
            redirectToProfile:false,
            fileSize:0,
            loading:false,
            about:""
        }
    }
    signupForm=(name,email,password,about)=> (    <form> 
        <div className="form-group">
                <label className="text-muted">Profile photo</label>
                <input 
                onChange={this.handleChange("photo")}
                  type="file" aceept="image/*" className="form-control"/>
            </div>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input onChange={this.handleChange("name")}  value={name} type="text" className="form-control"/>
            </div>
            <div className="form-group">
                <label className="text-muted">Email</label>
                <input onChange={this.handleChange("email")} value={email}  type="email" className="form-control"/>
            </div>
            <div className="form-group">
                <label className="text-muted">Password</label>
                <input onChange={this.handleChange("password")} value={password}  type="password" className="form-control"/>
            </div>

            <div className="form-group">
                <label className="text-muted">Password</label>
                <textarea onChange={this.handleChange("about")} 
                value={about}  type="textarea" className="form-control"/>
            </div>

            <button onClick={this.clickSubmit}className="btn btn-raised btn-primary">UPDATE</button>
        </form>
)    



handleChange=(name)=> (event) => {
    this.setState({error:""})
    const value= name==="photo" ?event.target.files[0]:event.target.value
    const fileSize= name==="photo" ? event.target.files[0].size : 0;
    this.userData.set(name,value)

        this.setState(
    {[name]:value,fileSize} //name has valuer of name,email and passeword
    )

}

 clickSubmit=event =>{
     event.preventDefault()// prevents page reloaad
     this.setState({loading:true})
     if(this.isValid()){

     const userId=this.props.match.params.userId;
     const token=isAuthenticated().token;

update(userId,token,this.userData)
    .then(data=>{
            if(data.error)
            {
                this.setState({error: data.error});

            }
            else{
                this.setState({

                    redirectToProfile:true
                })
            }
        })
    .catch(err=> console.log(err))

    }
}


    init =(userId) =>{
        const token=isAuthenticated().token
        read(userId,token)
        .then(data =>{
            if(data.error){
                this.setState({
                    redirectToProfile:true
                })
            }
            else{
                this.setState({id: data._id,name:data.name,email:data.email,error:""})
            }
        })
        .catch(err=>console.log(err))
    }


    componentDidMount()
    {
        this.userData=new FormData();
        const userId=this.props.match.params.userId;
        this.init(userId);
    } 

    isValid=() =>{
        const {name,email,password,fileSize}=this.state
        if(fileSize>1000000)
        {
            this.setState({error:"File size should be less than 1 MB",
            loading:false
            })

            return false;
        }
        if(name.length===0)
        {
            this.setState({error:"Name is required",
            loading:false
            })

            return false;
        }
        if(!/^\w+([\.-]?\w+)*@\w+([\,-]?\w+)*(\.\w{2,3})+$/.test(email))
        {
            this.setState({
                error:"A valid email is required",
                loading:false
            })
            return false;
        }
        if(password.length >=1 && password.length<=5)
        {
            this.setState({error:"Password must be atleast 6 characters long ", loading:false})

            return false;
        }
        return true;
    }

    render(){
        const{name,about,loading,email,password,id,error,redirectToProfile}=this.state;
        if(redirectToProfile){
            return <Redirect to={`/user/${id}`} />
        }

        const photoUrl=id ? `${process.env.REACT_APP_API_URL}/user/photo/${id}?${new Date().getTime()}` : DefaultProfile
        console.log(photoUrl);
        return(
            <div className="container">

              <h2 className="mt-5 mb-5">Profile</h2>
              <div className="alert alert-primary"
                 style={{display: error? "" :"none"}}>  
                         {error}
                 </div>
                 {loading ? (<div className="jumbotron text-centre"> Loading</div>):("")}
            <img src={photoUrl}
           alt={name}/>
              {this.signupForm(name,email,password,about)}
            </div>
        )
    }
}
export default EditProfile
