import React,{Component} from 'react'


class Signup extends Component{

    constructor(){
        super();
        this.state={
            name:"",
            email:"",
            password:"",
            error:""
        }
    }
//event we get when onChange is called //handle change is a hugher order function which returns another function
    handleChange=(name)=> (event) => {
        this.setState({error:""})
        this.setState(
        {[name]:event.target.value} //name has valuer of name,email and passeword
        )

    }

     clickSubmit=event =>{
         event.preventDefault()// prevents page reloaad
         const {name,email,password}=this.state;
         const user={
             name:name,
             email:email,
             password:password
         };

            this.signup(user)
            .then(data=>{
                if(data.error)
                {
                    this.setState({error:data.error})
                }
                else{
                    this.setState({
                        name:"",
                        error:"",
                        email:"",
                        password:""
                    })
                }
            })
        }

     signup=user =>{
        return fetch("http://localhost:8080/signup",{
            method:"POST",
            headers:{
                Accept:"application/json",
                "Content-Type":"application/json"
            },
            body:JSON.stringify(user)
        })
        .then(response =>{
            return response.json()
        })
        .catch(err=>console.log(err))
        //fetch is standard http client that is  used to make a post request 
       // console.log(user);

    };



    render(){
        return(
            <div className="container">
                <h2 className="mt-5 mb-5"> Signup</h2>
                <div className='alert alert-primary'
                 style={{display: error ? "" :"none"}}>

                 </div>
            <form> 
            <div className="form-group">
                    <label className="text-muted">Name</label>
                    <input onChange={this.handleChange("name")}  value={this.state.name} type="text" className="form-control"/>
                </div>
                <div className="form-group">
                    <label className="text-muted">Email</label>
                    <input onChange={this.handleChange("email")} value={this.state.email}  type="email" className="form-control"/>
                </div>
                <div className="form-group">
                    <label className="text-muted">password</label>
                    <input onChange={this.handleChange("password")} value={this.state.password} type="password" className="form-control"/>
                </div>

                <button onClick={this.clickSubmit}className="btn btn-raised btn-primary">SUBMIT</button>
            </form>

            </div>
        )
    }
}

export default Signup;

//install react-router-dom to create pages
